    {
        $this->selectedRows[$title]['type'] = $type;

        $this->chartType = $type;

        $this->generateChartData();
    }

    public function generateChartData($initChart = false): void
    {
        $chartData = [];
        foreach ($this->selectedRows as $title => $row) {
            $data = [];
            foreach ($row['dates'] as $key => $cell) {

                if (!$cell['empty']) {
                    $selectedYear = date('Y', strtotime($cell['date']));
                    $startYear = !is_numeric($this->startDate) ? date('Y', strtotime($this->startDate)) : $this->startDate;
                    $endYear = !is_numeric($this->endDate) ? date('Y', strtotime($this->endDate)) : $this->endDate;
                    if(!($selectedYear < $startYear) && !($selectedYear > $endYear)) {
                        $data[] = [
                            'y' => $cell['value'],
                            'x' => $cell['date'],
                        ];
                    }
                } else {
                    $data[] = [
                        'y' => null,
                        'x' => null,
                    ];
                }
            }
            $chartData[] = [
                'data' => $data,
                'type' => $row['type'],
                'label' => $title,
                'borderColor' => $row['color'],
                'pointRadius' => 1,
                'pointHoverRadius' => 8,
                'tension' => 0.5,
                'fill' => true,
                'pointHoverBorderColor' => '#fff',
                'pointHoverBorderWidth' => 4,
                'pointHoverBackgroundColor' => 'rgba(104, 104, 104, 0.87)'
            ];
        }

        $this->chartData = $chartData;

        if ($initChart) {
            $this->emit('initCompanyReportChart');
        } else {
            $this->emit('updateCompanyReportChart');
        }
    }

    public function getData() : void
    {
        $acronym = ($this->period == 'annual') || ($this->period == 'Calendar Annual') || ($this->period == 'Fiscal Annual') ? 'arf5drs' : 'qrf5drs';

        try {
            if ($this->view === 'Standardised') {
                $data = InfoTikrPresentation::where('ticker', $this->ticker)->orderByDesc('id')->first()->info;
            } else {
                $query = InfoPresentation::query()
                    ->where('ticker', '=', $this->ticker)
                    ->where('acronym', '=', $acronym)
                    ->where('id', '=', $this->activeSubIndex)
                    ->select('info')->value('info');

                $data = json_decode($query, true);

            }

            if ($this->activeTabName === 'Ratios') {
                $data = json_decode($this->fakeDataForRatiosPage(), true);
            }
            $this->noData = false;
        }
        catch(\Exception $e) {
            $this->noData = true;
            return;
        }

        // handle period is not set by default we put it to annual
        $this->period ?? $this->period = 'annual';

        // adjust data in case of Standardised and annual period or quarterly period since both are combined in one array
        $data = ($this->view === 'Standardised') ? (($this->period === 'annual' || $this->period === 'Fiscal Annual' || $this->period === 'Calendar Annual') ? $data['annual'] : $data['quarter']) : $data;

        $this->data = $data;
        $this->generateUI();
    }

